(function() {
    $(document).ready(function(){
        
        /* herder & footer*/
        $("#header").load("./inc/header.html", function(){ })
        $("#footer").load("./inc/footer.html")
            
                
        
        /* form 요소 radio & checkbox */
        $('.from_g input[type="radio"]').ezMark({radioCls: 'ez-radio', selectedCls: 'ez-radio_on'})
        $('.from_g input[type="checkbox"]').ezMark({checkboxCls: 'ez-checkbox', checkedCls: 'ez-checkbox_on'})
        
        /* form 요소 select-acbox  */
        $('.select-acbox').selectize({
            create: true,
            sortField: {
                field: 'text',
                direction: 'asc' 
            }
        });
        
        /* 검색 취소버튼 search-wrap */
        $(".search-wrap .cancel").on("click", function(){            
            $(".search-wrap .search-inner input").val("");
        })
        
        
   /*     $(".from_s .select-sort").on("focus", function($e){
            $(this).parent().addClass("on");
        })
        $(".from_s .select-sort").on("bluer", function($e){
            $(this).parent().removeclass("on");
        })*/
        
        
        /* 카테 고리 메뉴 드롭다운버튼  category.html, system_category.html */
        var category_nav;
        var category_h = 31;    // 1줄 높이 
        var category_line = $(".category_nav-wrap .category-list>li").length / 4;        
        category_nav =  $(".category_nav-wrap .dropdown-btn").trpToggleBtn( 
            function( $this){				
                $($this).addClass("on");
                $(".category_nav-wrap").animate({"height":category_h*category_line},370);
            },function( $this){				
                //$($this).removeClass("on");
                
                $(".category_nav-wrap").animate({"height":category_h},200);
        })
         
        /* == 텝메뉴 == detail_view.html */
        $(".tabs").trpTabMenu(".btn",".cons" ,"on");   
        
        
        
        /* 팝업 닫기 버튼 popup_wrap  (popup.html) */
        $(".popup_wrap .btn_close").on("click", function(){          
            $(".popup_wrap").hide();
        })
        
        /* 팝업 닫기 버튼 popup_wrap  (popup.html) */
        $(".popup_wrap.event .menu").on("click", function(){
            $(this).parent().siblings().removeClass("on");
            $(this).parent().addClass("on");
            
        })
        
        
    })
})();;(function() { 
    console.log("main.js");  
})(); 